import { useState, useEffect } from "react"
import { useNavigate, useParams } from "react-router-dom"
import "./BookingForm.css"

export const RequestEdit = () => {

    const { requestId } = useParams()

    const [editRequests, setEditRequest] = useState({
        bandName: "",
        // phoneNumber: "",
        // genreId: 0,
        // numOfSongs: "",
        // startDate: "",
        // endDate: "",
        // musicianRequest: false,
        // sessionMusicians: false,
        // photoRequest: false,
        // videoRequest: false,
        // clientId: 0

    })

    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre/${requestId}`)
                .then(response => response.json())
                .then((requestObject) => {
                    setEditRequest(requestObject)
                })
        },
        [requestId]
    )

    const handleSaveButtonClick = (event) => {
        event.preventDefault()

        return fetch(`http://localhost:8088/requests?_expand=genre`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(editRequests)
        })

            .then((response) => response.json)
            .then(() => {
                navigate("/myrequest")
            })

    }

    return (
        <>
            <form className="editRequest">

                <fieldset >
                    <div className="form-group">
                        <label htmlFor="bandName">Band Name: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={editRequests.bandName}
                            onChange={
                                (event) => {
                                    const copy = { ...editRequests }
                                    copy.bandName = event.target.value
                                    setEditRequest(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                <fieldset >
                    <div className="form-group">
                        <label htmlFor="phoneNumber">Phone Number: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={editRequests.phoneNumber}
                            onChange={
                                (event) => {
                                    const copy = { ...editRequests }
                                    copy.phoneNumber = event.target.value
                                    setEditRequest(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                <fieldset>
                    <div className="form-group">
                        <label htmlFor="genre">Choose a genre:</label>
                        <select id="genre"  required
                            onChange={
                                (event) => {
                                    const copy = { ...editRequests }
                                    copy.genreId = parseInt(event.target.value)
                                    setBookingForm(copy)
                                }}>
                            <option value="0">Genre...</option>

                            {
                                genres.map(
                                    (genre) => {
                                        return <option
                                            key={`genre--${genre.id}`}
                                            value={genre.id}>{genre.type}</option>
                                    }
                                )
                            }
                        </select>

                    </div>
                </fieldset>


                <button
                    onClick={(clickEvent) => { handleSaveButtonClick(clickEvent) }}
                    className="btn btn-primary">
                    Save Edits
                </button>
            </form>
        </>
    )
}
