import { useState, useEffect } from "react"
import { useNavigate, useParams } from "react-router-dom"
import "./BookingForm"

export const MyRequest = () => {

    const { clientId } = useParams()
    const [requests, setRequest] = useState([])
    const [client, setClients] = useState({})
    const navigate = useNavigate()


    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(genreInfo)
                })
        }, []
    )


    useEffect(
        () => {
            fetch(`http://localhost:8088/clients?_expand=user`)
                .then(response => response.json())
                .then((clientInfo) => {
                    const singleClient = clientInfo[0]
                    setClients(singleClient)
                })
        },
        [clientId]
    )

    const [editRequests, setEditRequest] = useState({
        bandName: ""
        // phoneNumber: "",
        // genreId: 0,
        // numOfSongs: "",
        // startDate: "",
        // endDate: "",
        // musicianRequest: false,
        // sessionMusicians: false,
        // photoRequest: false,
        // videoRequest: false,
        // clientId: 0

    })


    const handleEditButtonClick = (event) => {
        event.preventDefault()

        return fetch(`http://localhost:8088/clients?&_embed=requests`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requests)
        })

            .then((response) => response.json)
            .then(() => {
                navigate("myrequest/:requestId/edit")
            })

    }

    const handleDeleteButtonClick = (event) => {
        event.preventDefault()

        return fetch(`http://localhost:8088/clients?&_embed=requests`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requests)
        })

            .then((response) => response.json)
            .then(() => {
                navigate("/myrequest/requestId/delete")
            })

    }

    return (
        <div className="myrequest">

            <h2>My Request</h2>
            <div>Band Name: {requests.bandName}</div>
            <div>Start Date: {requests.startDate}</div>
            <div>End Date: {requests.endDate}</div>


            <button
                onClick={(clickEvent) => { handleEditButtonClick(clickEvent) }}
                className="btn btn-primary">
                Edit Request
            </button>

            <button
                onClick={(clickEvent) => { handleDeleteButtonClick(clickEvent) }}
                className="btn btn-primary">
                Delete Request
            </button>
        </div>

    )
}
