export const AcceptButton = ({ requests }) => {

    const acceptRequestButton = (event, requests) => {
        event.preventDefault()

        const requestAcceptStatusForAPI = {
            isAccepted: true,
            isPending: false,
        }

        fetch(`http://localhost:8088/requests/${requests}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requestAcceptStatusForAPI)
        })
            .then(response => response.json())
            .then(
                setRefresh(!refresh)
            )
    }

    return (
        <>

            {requests.isAccepted === true
                ? <div id="block">Added to <Link to="/calendar">üóìÔ∏è</Link></div>
                :

                <button
                    onClick={(clickEvent) => { acceptRequestButton(clickEvent, request.id) }}
                    className="acceptButton" id="button">
                    Accept Request
                </button>
            }
        </>
    )
}
