import { useEffect, useState } from 'react'
import { useNavigate, useParams } from "react-router-dom"
import "./Gallery.css"

export const GalleryView = () => {

    const [photos, setPhotos] = useState([])

    const {galleryId} = useParams()

    const localFatBackUser = localStorage.getItem("fatback_user")
    const fatbackUserObject = JSON.parse(localFatBackUser)

    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/gallery/$`)
                .then(response => response.json())
                .then((photoGallery) =>
                    setPhotos(photoGallery))
        },
        []
    )

    const getAllImages = () => {

        fetch(`http://localhost:8088/gallery`)
            .then(response => response.json())
            .then((photoGallery) => {
                setPhotos(photoGallery)
            })
    }

    const handleDeleteImageButton = () => {

        if (fatbackUserObject.staff) {
            return <button id="button"
                onClick={() => {
                    fetch(`http://localhost:8088/gallery/${galleryId}`, {
                        method: "DELETE"
                    })
                        .then(() => {
                            getAllImages()
                        })
                }}>Delete</button>
        }
        else {
            return ""

        }

    }

    const addPhoto = (event) => {
        event.preventDefault()

        navigate(`/gallery/addgalleryimage`)

    }

    return (
        <div className="galleryPage">
            <h3 className="galleryHeader">Gallery</h3>
            <div className="gallery">
                {
                    photos.map(photo => {
                        return <div><img className="galleryImg" src={photo.imageURL} />
                            {handleDeleteImageButton()}
                        </div>

                    }
                    )
                }
            </div>


            {
                fatbackUserObject.staff
                    ?
                    <div className="button">
                        <button
                            onClick={(clickEvent) => { addPhoto(clickEvent, photos.id) }}
                            className="editButton">
                            Add New Photos
                        </button>


                    </div> :
                    <></>
            }
        </div>
    )
}
