import "./Requests.css"

export const ContactNotice = ({ id }) => {

    const [contact, update] = useState({
        contacted: false
    })

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?isAccepted=true`)
                .then(response => response.json())
                .then((requestInfo) => {
                    update(requestInfo)
                })
        }, []
    )

    const handleSaveContactCheck = (event) => { 
