import { useState, useEffect } from "react"

export const DenyButton = () => {

    const [requests, setRequest] = useState([])
    const [refresh, setRefresh] = useState(true)


    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(requestInfo)
                })
        }, [, refresh]
    )

    const denyRequest = (event, requests) => {
        event.preventDefault()

        const requestDenyStatusForAPI = {
            isDenied: true,
            isPending: false,
        }

        fetch(`http://localhost:8088/requests/${requests.id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requestDenyStatusForAPI)
        })
            .then(response => response.json())
            .then(
                <button
                    onClick={(clickEvent) => { denyRequest (clickEvent, requests.id) }}
                    className="acceptButton" id="button">
                    Accept Request
                </button>
            )
            .then(
                setRefresh(!refresh)
            )

    }

    return (
        <>

            {
                requests.isDenied === true
                    ? <div>‚ùå Request has been denied.</div> :
                    <button
                        onClick={(clickEvent) => { denyRequest(clickEvent, requests.id) }}
                        className="denyButton" id="button" >
                        Deny Request
                    </button>
            }
        </>
    )
}
