import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import "./Staff.css"

export const AddStaff = () => {

    const [newStaff, setNewStaff] = useState({

    })

    useEffect(() => {

    })

    const handleSaveNewStaffButton = (event) => {
        event.preventDefault()

        const newArtistToSendToAPI = {
            artistName: newStaff.artistName,
            imageURL: newStaff.imageURL,
            startDate: newStaff.startDate
        }

        return fetch(`http://localhost:8088/artists`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(newArtistToSendToAPI)
        })

            .then((response) => response.json())
            .then(() => {
                navigate("/artists")
            })

    }

    return (
        <>
            <h2 className="addStaffHeader">Add New Staff Member</h2>
            <form>
                <fieldset >
                    <div className="form-group">
                        <label className="label" htmlFor="name">Name: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={newStaff.fullName}
                            onChange={
                                (event) => {
                                    const copy = { ...newStaff }
                                    copy.fullName = event.target.value
                                    setNewStaff(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                <fieldset >
                    <div className="form-group">
                        <label className="label" htmlFor="email">Email: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={newStaff.email}
                            onChange={
                                (event) => {
                                    const copy = { ...newStaff }
                                    copy.email = event.target.value
                                    setNewStaff(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>
                <fieldset >
                    <div className="form-group">
                        <label className="label" htmlFor="startDate">Start Date: </label>
                        <input
                            required autoFocus
                            type="date"
                            className="form-control"
                            value={newStaff.startDate}
                            onChange={
                                (event) => {
                                    const copy = { ...newStaff }
                                    copy.startDate = event.target.value
                                    setNewStaff(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                <fieldset >
                    <div className="form-group">
                        <label className="label" htmlFor="image">Image: </label>
                        <input
                            required autoFocus
                            type="texxt"
                            className="form-control"
                            placeholder="example.com"
                            value={newStaff.imageURL}
                            onChange={
                                (event) => {
                                    const copy = { ...newStaff }
                                    copy.imageURL = event.target.value
                                    setNewStaff(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                <button
                    className="saveNewStaffButton"
                    onClick={(event) => {
                        handleSaveNewStaffButton(event)
                    }}
                >
                    Add New Staff
                </button>
            </form>
        </>
    )
}
