import { useEffect, useState } from 'react'
import { useNavigate } from "react-router-dom"
import "./Artist.css"

export const ArtistList = ({ searchTermState }) => {

    const [artists, setArtists] = useState([])
    const [filteredArtists, setFilteredArtists] = useState([])

    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/artists`)
                .then(response => response.json())
                .then((artistArray) => {
                    setArtists(artistArray)
                })
        },
        []
    )

    useEffect(
        () => {
            setFilteredArtists(artists)

        },
        [artists]
    )

    useEffect(
        () => {
            const searchedArtist = artists.filter(artist => {
                return artist.bandName.toLowerCase().startsWith(searchTermState.toLowerCase())
            }
            )
            setFilteredArtists(searchedArtist)
        },
        [searchTermState]

    )

    const addArtistButton = (event, artist) => {
        event.preventDefault()

        navigate(`/myrequest/${artist}`)

    }

    return (
        <div className="artistPage">
            <h2 className="artistHeader">Artists</h2>
            <div className="artists">
                {
                    filteredArtists.map((artist) => {
                        return <div className="artist" key={`artist--${artist.id}`}>
                            <img src={artist.imageURL} />
                            <div>{artist.bandName}</div>
                        </div>
                    })
                }
            </div>

            <div className="button">
                <button
                    onClick={(clickEvent) => { addArtistButton(clickEvent, request.id) }}
                    className="editButton">
                    Add New Artist
                </button>


            </div>

        </div>


    )
}
