import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import "./BookingForm"

export const MyRequest = () => {

    const [requests, setRequest] = useState([])
    // const [client, setClients] = useState({})
    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(requestInfo)
                })
        }, []
    )



    // const [editRequests, setEditRequest] = useState({
    //     bandName: "",
    //     phoneNumber: "",
    //     genreId: 0,
    //     numOfSongs: "",
    //     startDate: "",
    //     endDate: "",
    //     musicianRequest: false,
    //     sessionMusicians: false,
    //     photoRequest: false,
    //     videoRequest: false,
    //     clientId: 0

    // })


    const handleEditButtonClick = (event) => {
        event.preventDefault()

        return fetch(`http://localhost:8088/requests?_expand=genre&_expand=user`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requests)
        })

            .then((response) => response.json())
            .then(() => {
                navigate("/myrequest")
            })

    }

    const handleDeleteButtonClick = (event) => {
        event.preventDefault()

        return fetch(`http://localhost:8088/requests?_expand=genre&_expand=user`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requests)
        })

            .then((response) => response.json)
            .then(() => {
                navigate("/myrequest")
            })

    }

    return (
        <div className="myrequest">

            <h2>My Request</h2>
            <div key={`requests--${requests.id}`}>
            {requests.map(request => {
                return (<>
                   <div className="requestInfo" key={`request--${request.id}`}>
                        <div>Band Name: {request.bandName}</div>
                        <div>Phone Number: {request.phoneNumber}</div>
                        <div>Start Date: {request.startDate}</div>
                        <div>End Date: {request.endDate}</div>
                        <div>No. of Songs: {request.numOfSongs}</div>
                        <div>Genre: {request?.genre?.type}</div>
                    </div>
                </>)
            })}
            </div>

            <button
                onClick={(clickEvent) => { handleEditButtonClick(clickEvent) }}
                className="editButton">
                Edit Request
            </button>

            <button
                onClick={(clickEvent) => { handleDeleteButtonClick(clickEvent) }}
                className="deleteButton">
                Delete Request
            </button>
        </div >

    )
}
