import { useState, useEffect } from "react"
import { useNavigate, useParams } from "react-router-dom"
import "./BookingForm.css"

export const RequestEdit = () => {

    // { genrearray }

    const { requestId } = useParams()

    const [editRequests, setEditRequest] = useState({

        bandName: "",
        phoneNumber: "",
        //genreId: 0,
        numOfSongs: "",
        startDate: "",
        endDate: "",
        musicianRequest: false,
        sessionMusicians: false,
        photoRequest: false,
        videoRequest: false

    })

    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests/${requestId}`)
                .then(response => response.json())
                .then((requestObject) => {
                    setEditRequest(requestObject)
                })
        },
        []
    )

    const handleSaveButtonClick = (event) => {
        event.preventDefault()

        return fetch(`http://localhost:8088/requests/${requestId}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(editRequests)
        })

            .then((response) => response.json())
            .then(() => {
                navigate("/myrequest")
            })

    }

    return (
        <>
            <form className="editRequest popup" id="popup">

                <fieldset >
                    <div className="form-group">
                        <label htmlFor="bandName">Band Name: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            defaultValue={editRequests.bandName}
                            onChange={
                                (event) => {
                                    const copy = { ...editRequests }
                                    copy.bandName = event.target.value
                                    setEditRequest(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>




                    <fieldset>
                        <div className="form-group">
                            <label className="label" htmlFor="phoneNumber">
                                Phone Number:
                            </label>
                            <input
                                required autoFocus
                                type="text"
                                className="form-control"
                                defaultValue={editRequests.phoneNumber}
                                onChange={
                                    (event) => {
                                        const copy = { ...editRequests }
                                        copy.phoneNumber = event.target.value
                                        setEditRequest(copy)
                                    }
                                }
                            />

                        </div>
                    </fieldset>

                    {/* <fieldset>
                        <div className="form-group">
                            <label htmlFor="genre">Choose a genre:</label>
                            <select id="genre" required
                                onChange={
                                    (event) => {
                                        const copy = { ...editRequests }
                                        copy.genreId = parseInt(event.target.value)
                                        setEditRequest(copy)
                                    }}>
                                <option value="0">Genre...</option>

                                {
                                     genrearray.map(
                                        (genre) => {
                                            return <option
                                                key={`genre--${genre.id}`}
                                                value={genre.id}>{genre.type}</option>
                                        }
                                    )
                                }
                            </select>

                        </div>
                    </fieldset> */}

                    <fieldset>
                        <div className="form-group">
                            <label className="label" htmlFor="songs">
                                No. of Songs:
                            </label>
                            <input
                                required autoFocus
                                type="text"
                                className="form-control"
                                value={editRequests.numOfSongs}
                                onChange={
                                    (event) => {
                                        const copy = { ...editRequests }
                                        copy.numOfSongs = event.target.value
                                        setEditRequest(copy)
                                    }
                                }

                            />

                        </div>
                    </fieldset>

                    <fieldset>
                        <div className="form-group">
                            <label className="label" htmlFor="date">
                                Start Date:
                            </label>
                            <input

                                type="date"
                                required
                                className="form-control"
                                defaultValue={editRequests.startDate}
                                onChange={
                                    (event) => {
                                        const copy = { ...editRequests }
                                        copy.startDate = event.target.value
                                        setEditRequest(copy)
                                    }
                                }

                            />

                        </div>
                    </fieldset>

                    <fieldset>
                        <div className="form-group">
                            <label className="label" htmlFor="date">
                                End Date:
                            </label>
                            <input
                                required
                                type="date"
                                className="form-control"
                                defaultValue={editRequests.endDate}
                                onChange={
                                    (event) => {
                                        const copy = { ...editRequests }
                                        copy.endDate = event.target.value
                                        setEditRequest(copy)
                                    }
                                }

                            />

                        </div>
                    </fieldset>

                    <fieldset>
                        <div className="form-group">
                            <label htmlFor="band">
                                Do you have musicians or a band?
                            </label>

                            <div className="radioButton">
                                <input
                                    required autoFocus
                                    type="radio"
                                    className="radioButton"
                                    defaultValue={editRequests.musicianRequest}
                                    name="musicians"
                                    onChange={
                                        (event) => {
                                            const copy = { ...editRequests }
                                            copy.musicianRequest = event.target.checked
                                            setEditRequest(copy)
                                        }
                                    }
                                />
                                <label className="radioLabel">Yes</label>

                                <input
                                    required autoFocus
                                    type="radio"
                                    className="radioButton"
                                    defaultValue={editRequests.musicianRequest}
                                    name="musicians"
                                    onChange={
                                        (event) => {
                                            const copy = { ...editRequests }
                                            copy.musicianRequest = event.target.checked
                                            setEditRequest(copy)
                                        }
                                    }
                                />
                                <label className="radioLabel" >No</label>

                            </div>
                        </div>
                    </fieldset>

                    <fieldset>
                        <div className="form-group">

                            Would you like session musicians?

                            <div className="radioButton">
                                <input
                                    required autoFocus
                                    type="radio"
                                    className="radioButton"
                                    defaultValue={editRequests.sessionMusicians}
                                    id="yes"
                                    name="session"
                                    onChange={
                                        (event) => {
                                            const copy = { ...editRequests }
                                            copy.sessionMusicians = event.target.checked
                                            setEditRequest(copy)
                                        }
                                    }
                                />
                                <label className="radioLabel"
                                >Yes</label>

                                <input
                                    required autoFocus
                                    type="radio"
                                    className="radioButton"
                                    defaultValue={editRequests.sessionMusicians}
                                    id="no"
                                    name="session"
                                    onChange={
                                        (event) => {
                                            const copy = { ...editRequests }
                                            copy.sessionMusicians = event.target.checked
                                            setEditRequest(copy)
                                        }
                                    }
                                />
                                <label className="radioLabel"
                                >No</label>

                            </div>

                        </div>
                    </fieldset>

                    <fieldset>
                        <div className="form-group">

                            Do you want photos?

                            <div className="radioButton">
                                <input
                                    required autoFocus
                                    type="radio"
                                    className="radioButton"
                                    defaultValue={editRequests.photoRequest}
                                    id="yes"
                                    name="photo"
                                    onChange={
                                        (event) => {
                                            const copy = { ...editRequests }
                                            copy.photoRequest = event.target.checked
                                            setEditRequest(copy)
                                        }
                                    }
                                />
                                <label className="radioLabel">Yes</label>

                                <input
                                    required autoFocus
                                    type="radio"
                                    className="radioButton"
                                    defaultValue={editRequests.photoRequest}
                                    id="no"
                                    name="photo"
                                    onChange={
                                        (event) => {
                                            const copy = { ...editRequests }
                                            copy.photoRequest = event.target.checked
                                            setEditRequest(copy)
                                        }
                                    }
                                />
                                <label className="radioLabel">No</label>

                            </div>

                        </div>
                    </fieldset>

                    <fieldset>
                        <div className="form-group">
                            Do you want video?

                            <div className="radioButton">
                                <input
                                    required autoFocus
                                    type="radio"
                                    className="radioButton"
                                    defaultValue={editRequests.videoRequest}
                                    id="yes"
                                    name="video"
                                    onChange={
                                        (event) => {
                                            const copy = { ...editRequests }
                                            copy.videoRequest = event.target.checked
                                            setEditRequest(copy)
                                        }
                                    }
                                />
                                <label className="radioLabel"
                                >Yes</label>

                                <input
                                    required autoFocus
                                    type="radio"
                                    className="radioButton"
                                    defaultValue={editRequests.videoRequest}
                                    id="no"
                                    name="video"
                                    onChange={
                                        (event) => {
                                            const copy = { ...editRequests }
                                            copy.videoRequest = event.target.checked
                                            setEditRequest(copy)
                                        }
                                    }
                                />
                                <label className="radioLabel"
                                >No</label>

                            </div>
                        </div>
                    </fieldset>



                    <button
                        onClick={(clickEvent) => {
                            handleSaveButtonClick(clickEvent)
                        }}
                        className="saveEditButton">
                        Save Changes
                    </button>
                </form>
            </>
            )
}
