import { useEffect, useState } from 'react'

export const ContactNotice = () => {


    const [contact, update] = useState({
        contacted: false
    })

   const updateRequest = { 
        contacted: contact.contacted
   }

   return fetch("http://localhost:8088/requests?_expand=genre&_sort=startDate", {
    method: "PATCH", 
    headers: {
        "Content-Type": "application/json", 
    }, 
    body: JSON.stringify(updateRequest)
}) 

    .then ((response) => response.json())

    // TODO: Perform the fetch() to POST the object to the API



    return (
        <>
            <label>Email sent?</label>
            <input
                type="checkbox"
                value={contact.contacted}
                onChange={
                    (event) => {
                        const copy = { ...contact }
                        copy.contacted = event.target.checked
                        update(copy)
                    }
                } />
        </>
    )
}
