import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import "./BookingForm.css"

export const BookingForm = () => {

    // const [genres, setGenres] = useState([])

    const [request, setBookingForm] = useState({

        bandName: ""

    })

    const navigate = useNavigate()

    // const localFatBackUser = localStorage.getItem("fatback_user")
    // const fatbackUserObject = JSON.parse(localFatBackUser)

    // useEffect(
    //     () => {
    //         fetch(`http://localhost:8088/genres`)
    //             .then(response => response.json())
    //             .then((genreArray) => {
    //                 setGenres(genreArray)
    //             })
    //     },
    //     []

    // )


    const submitButtonClick = (event) => {
        event.preventDefault()

        const requestFormToSendToAPI = {

            bandName: request.bandName

        }

        return fetch(`http://localhost:8088/requests`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requestFormToSendToAPI)
        })

            .then(response => response.json())
            .then(() => {
                navigate("/myrequest")
            })

    }

    return (
        < div className="bookingForm" >
            <h2>Booking Form</h2>
            <form>

                <fieldset >
                    <div className="form-group">
                        <label htmlFor="bandName">Band Name: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={request.bandName}
                            onChange={
                                (event) => {
                                    const copy = { ...request }
                                    copy.bandName = event.target.value
                                    setBookingForm(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                <fieldset>
                    <div className="form-group">
                        <label htmlFor="phoneNumber">
                            Phone Number:
                        </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            placeholder="Your number..."
                            value={bookingForm.phoneNumber}
                            onChange={
                                (event) => {
                                    const copy = { ...bookingForm }
                                    copy.phoneNumber = event.target.value
                                    setBookingForm(copy)
                                }
                            }
                        />

                    </div>
                </fieldset>


                <button
                    onClick={(clickEvent) => {
                        submitButtonClick(clickEvent)
                    }}
                    className="submitButton">
                    Submit Request
                </button>


            </form>
        </div >
    )
}
