import { useEffect, useState } from 'react'
import { useNavigate } from "react-router-dom"
import "./Requests.css"

export const AddNotes = ({ id }) => {

    const [notes, setNotes] = useState({

    })

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests/${id}`)
                .then(response => response.json())
                .then((staffNotes) => {
                    setNotes(staffNotes)
                })
        }, []
    )

    const navigate = useNavigate()

    const handleSaveNotes = (event) => {
        event.preventDefault()

        const newStaffNote = {
            notes: notes.notes
        }

        return fetch(`http://localhost:8088/requests/${id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(newStaffNote)
        })

            .then((response) => response.json())
            .then(() => {
                navigate("/requests")
            })

    }


    return (
        <div className="notes">
            <form>
                <textarea
                    id="staffNotes"
                    placeholder="Add notes..."
                    value={notes.notes}
                    onChange={(event) => {
                        const copy = { ...notes }
                        copy.notes = event.target.value
                        setNotes(copy)
                    }}>
                </textarea>

                {notes.length ?
                    <button
                        className="saveNotesButton"
                        onClick={(event) => {
                            handleSaveNotes(event)
                        }}>
                        Save Note
                    </button> : <button
                        className="saveNotesButton"
                        onClick={(event) => {
                            handleSaveNotes(event)
                        }}>
                        Save Note
                    </button>}
            </form>
        </div>

    )
}

