import { Link } from "react-router-dom"
import { useState, useEffect } from "react"

export const AcceptButton = () => {

    const [requests, setRequest] = useState([])
    const [refresh, setRefresh] = useState(true)

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(requestInfo)
                })
        }, [, refresh]
    )

    const acceptRequestButton = (event, id) => {
        event.preventDefault()

        const requestAcceptStatusForAPI = {
            isAccepted: true,
            isPending: false,
        }

        fetch(`http://localhost:8088/requests/${id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requestAcceptStatusForAPI)
        })
            .then(response => response.json())
            .then(
                refresh(!refresh)
            )
    }

    return (
        <>
           {

                requests.isAccepted === true
                    ? <div>Added to <Link to="/calendar">üóìÔ∏è</Link></div>
                    :

                    <button
                        onClick={(clickEvent) => { acceptRequestButton(clickEvent, requests.id) }}
                        className="acceptButton" id="button">
                        Accept Request
                    </button>
            }
        </>
    )
}
