export const AcceptBooking = ({requests}) => { 
    const acceptRequestButton = (event, requests) => {
        event.preventDefault()
        const requestAcceptStatusForAPI = {
            isAccepted: true, 
            pending: false
        }

        return fetch(`http://localhost:8088/requests/${requests}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requestAcceptStatusForAPI)
        })
            .then(response => response.json())
            .then(() => {
                navigate("/requests")
            })
            .then(() => {
                getAllRequests(requests)
            }
            )


    }
    return( 
        <>
        {!requests.isAccepted === true 
        ? "Your request has been accepted!" : "" }
        </>
    )
}
