        fetch('http://localhost:8088/requests')

            .then(response => response.json())


            .then((pendingRequest => {
                setRequests(pendingRequest)
            }))

            .then(() => {
                getAllPendingRequests()
            })


            .then(() => {
                setRefresh(!refresh)
            })

            .then(() => { 
                windowAlert()
            })


    },
        [])

