import { useState, useEffect } from "react"
import { useNavigate, useParams } from "react-router-dom"
import "./BookingForm.css"

export const RequestEdit = () => {

    const { requestId } = useParams()

    const [editRequests, editRequest] = useState({
        bandName: "",
        phoneNumber: "",
        genreId: 0,
        numOfSongs: "",
        startDate: "",
        endDate: "",
        musicianRequest: false,
        sessionMusicians: false,
        photoRequest: false,
        videoRequest: false,
        clientId: 0

    })

    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/clients?&_embed=requests`)
                .then(response => response.json())
                .then((requestObject) => {
                    editRequest(requestObject)
                })
        },
        [requestId]
    )

    const handleEditButtonClick = (event) => {
        event.preventDefault()

        return fetch(`http://localhost:8088/clients?&_embed=requests`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requests)
        })

            .then((response) => response.json)
            .then(() => {
                navigate("/myrequest")
            })

    }

    return (
        <>
            <form className="editRequest">

                <fieldset >
                    <div className="form-group">
                        <label htmlFor="bandName">Band Name: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={requests.bandName}
                            onChange={
                                (event) => {
                                    const copy = { ...requests }
                                    copy.bandName = event.target.value
                                    editRequest(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>
                <button
                    onClick={(clickEvent) => { handleEditButtonClick(clickEvent) }}
                    className="btn btn-primary">
                    Save Edits
                </button>
            </form>
        </>
    )
}
