import { useEffect, useState } from 'react'

export const ContactNotice = ({ id }) => {


    const [contact, update] = useState({
        contacted: false
    })




    // TODO: Create the object to be saved to the API

    const updateContacted = (event) => {
        event.preventDefault()

        const updatedInfo = {
            isAccepted: true,
            isPending: false,
        }

        fetch(`http://localhost:8088/requests/${id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedInfo)
        })
            .then(response => response.json())

            .then(
                setRefresh(!refresh)
            )
    }


    return (
        <>
            <label>Email sent?</label>
            <input
                type="checkbox"
                value={contact.contacted}
                onChange={
                    (event) => {
                        const copy = { ...contact }
                        copy.contacted = event.target.checked
                        update(copy)
                    }
                } />
        </>
    )
}
