import { useState, useEffect } from "react"
import { useNavigate, useParams } from "react-router-dom"
import "./BookingForm"

export const MyRequest = () => {

    const { requestId } = useParams()

    const localFatBackUser = localStorage.getItem("fatback_user")
    const fatbackUserObject = JSON.parse(localFatBackUser)

    const [requests, setRequest] = useState([])
    // const [client, setClients] = useState({})
    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre&userId=${fatbackUserObject.id}`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(requestInfo)
                })
        }, []
    )

    const handleEditButtonClick = (event, request) => {
        event.preventDefault()


        navigate(`/myrequest/${request}`)

    }




    const deleteButton = () => {

            return <button
                onClick={() => 
                    fetch(`http://localhost:8088/requests?_expand=genre&userId=${fatbackUserObject.id}`, {
                        method: "DELETE"
                    })
                    .then(() => { 
                        navigate(`/home`)
                    })
                        }
                className="deleteButton">
                Delete Request</button>

    }

    // const handleDeleteButtonClick = (event) => {
    //     event.preventDefault()

    //     return fetch(`http://localhost:8088/requests?_expand=genre&userId=${fatbackUserObject.id}`, {
    //         method: "DELETE",
    //         headers: {
    //             "Content-Type": "application/json",
    //         },

    //     })


    //         .then(() => {
    //             navigate("/myrequest")
    //         })

    // }


    return (
                <div className="myrequest">

                    <h2 className="requestHeader">My Request</h2>

                    <>{requests.length ?
                        <div key={`requests--${requests.id}`}>
                            {requests.map(request => {
                                return (<>
                                    <div className="requestInfo" key={requestId}>
                                        <div>Band Name: {request.bandName}</div>
                                        <div>Phone Number: {request.phoneNumber}</div>
                                        <div>Start Date: {request.startDate}</div>
                                        <div>End Date: {request.endDate}</div>
                                        <div>No. of Songs: {request.numOfSongs}</div>
                                        <div>Genre: {request?.genre?.type}</div>

                                        <div>Musicians: 

                                            <input 
                                            type="radio"
                                            value={request.musicanRequest} /> 
                                            <label>Yes</label>

                                            <input 
                                            type="radio"
                                            value={request.musicanRequest} /> 
                                            <label>No</label>
                                        </div>


                                        {/* <div>
                                            <input type="checkbox"
                                               /><label> Musicians</label>
                                        </div>
                                        <div>
                                            <input type="checkbox"
                                               /><label> Session Musicians</label>
                                        </div>

                                        <div>
                                            <input type="checkbox"
                                               /><label> Photos</label>
                                        </div>

                                        <div>
                                            <input type="checkbox"
                                               /><label> Video</label>
                                        </div> */}
                                    </div>
                                    <div className="button">
                                        <button
                                            onClick={(clickEvent) => { handleEditButtonClick(clickEvent, request.id) }}
                                            className="editButton">
                                            Edit Request
                                        </button>

                                           { deleteButton()}

                                    </div></>)
                            })}
                        </div> :
                        <div>NO REQUEST...</div>
                    }</>

                </div >

                )
}
