import { useEffect, useState } from "react"

export const AdminGreeting = () => {


    const [requests, setRequests] = useState([])
    const [pendingRequests, setPendingRequests] = useState([])
    const [users, setUsers] = useState([])

    const localFatBackUser = localStorage.getItem("fatback_user")
    const fatbackUserObject = JSON.parse(localFatBackUser)

    useEffect(() => {
        fetch(`http://localhost:8088/users?isStaff=true`)
            .then(response => response.json())
            .then(userInfo => {
                setUsers(userInfo)
            })
    }, [])

    const getAllPendingRequests = () => {
        fetch(`http://localhost:8088/requests?isPending=true`)
            .then(response => response.json())
            .then((requestInfo) => {
                setPendingRequests(requestInfo)
            })
    }


    const [greeting, setGreeting] = useState([])

    useEffect(() => {
        if (requests.isPending = true) {
            return `Welcome ${users.fullName}. You have pending requests.`

        } else {
            return "Welcome. You have no pending requests"
        }

        // Clear feedback to make entire element disappear after 3 seconds
        setTimeout(() => setGreeting(""), 3000)
    }

        , [greeting])

    // const windowAlert = () => {
    //     <div className="adminGreeting">
    //         {
    //             requests.isPending = true ?
    //                 window.alert(
    //                     `Welcome ${users.fullName}. You have pending requests.`)
    //                 : window.alert("Welcome. You have no pending requests")
    //         }
    //     </div>

    // }

    useEffect(() => {

        fetch('http://localhost:8088/requests')
            .then(response => response.json())
            .then((pendingRequest => {
                setRequests(pendingRequest)
            }))

            .then(() => {
                getAllPendingRequests()
            })

            .then(() => {
                setGreeting()
            }
            )
    },
        [])

    return (<>
        <div className="greeting">
            {greeting}
        </div></>)

}
