import { useState, useEffect } from "react"

export const DenyButton = ({ id }) => {

    const [requests, setRequest] = useState([])
    const [refresh, setRefresh] = useState(true)


    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(requestInfo)
                })
        }, [, refresh]
    )

    const denyRequest = (event) => {
        event.preventDefault()

        const requestDenyStatusForAPI = {
            isDenied: true,
            isPending: false,
        }

        return fetch(`http://localhost:8088/requests/${id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requestDenyStatusForAPI)
        })
            .then(response => response.json())

            .then(
                setRefresh(!refresh)
            )

    }

    function refreshPage() {
        window.location.reload(false)
    }

    return (
        <>
            <button
                onClick={(clickEvent) => {

                    denyRequest(clickEvent)
                    {refreshPage()}
                    //setRefresh(!refresh)
                }}
                className="denyButton" id="button" >
                Deny Request
            </button>

            {/* {
                requests.isDenied === true
                    ? <div>‚ùå Request has been denied.</div> :
                    <button
                        onClick={(clickEvent) => { denyRequest(clickEvent, id) }}
                        className="denyButton" id="button" >
                        Deny Request
                    </button>
            } */}
        </>
    )
}
