import { useState, useEffect } from "react"
import { useNavigate, Link } from "react-router-dom"
//import { FutureBookings } from "./FutureBookings.js"
// import { AcceptButton } from "./AcceptButton.js"
// import { DenyButton } from "./DenyButton.js"
import { AcceptDenyContainer } from "./AcceptDenyContainer.js"
import "./BookingForm"


export const Requests = () => {

    const [requests, setRequest] = useState([])
    const [refresh, setRefresh] = useState(true)


    const navigate = useNavigate()

    // const localFatBackUser = localStorage.getItem("fatback_user")
    // const fatbackUserObject = JSON.parse(localFatBackUser)

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre&_sort=startDate`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(requestInfo)
                })
        }, [, refresh]
    )



    return (<>

        <div className="myrequests">
            {requests.length ?
                <div key={`requests--${requests.id}`}>
                    <h2 className="requestHeader">Studio Requests</h2>
                    <div className="requestBox">
                        {requests.map(request => {
                            return (<>
                                <div className="requestInfo" key={`requests--${request.id}`}>
                                    <div>Band Name: {request.bandName}</div>
                                    <div>Phone Number: {request.phoneNumber}</div>
                                    <div>Start Date: {request.startDate}</div>
                                    <div>End Date: {request.endDate}</div>
                                    <div>No. of Songs: {request.numOfSongs}</div>
                                    <div>Genre: {request?.genre?.type}</div>

                                    <div className="radioReview">Musicians:
                                        {request.musicianRequest === true ?
                                            <div>Yes</div> :
                                            <div>No</div>
                                        }

                                    </div>

                                    <div className="radioReview">Session Musicians:
                                        {request.sessionMusicians === true ?
                                            <div>Yes</div> :
                                            <div> No</div>
                                        }

                                    </div>

                                    <div className="radioReview">Photos:
                                        {request.photoRequest === true ?
                                            <div>Yes</div> :
                                            <div>No</div>
                                        }

                                    </div>

                                    <div className="radioReview">Video Request:
                                        {request.videoRequest === true ?
                                            <div>Yes</div> :
                                            <div>No</div>
                                        }

                                    </div>

                                        <br></br>
                                        <br></br>

                                    <div className="buttonContainer">
                                        <AcceptDenyContainer requests={request} id={request.id} />


                                        {/* {request.isAccepted || request.isDenied ?
                                            "" : <div className="buttonContainer"><AcceptButton requests={request} />
                                                <DenyButton requests={request} id={request.id} /></div>} */}

                                        {/* {request.isAccepted === true
                                            ? <div id="block">Added to <Link to="/calendar">🗓️</Link></div>
                                            :

                                            <button
                                                onClick={(clickEvent) => { acceptRequestButton(clickEvent, request.id) }}
                                                className="acceptButton" id="button">
                                                Accept Request
                                            </button>
                                        }

                                        {request.isDenied === true
                                            ? <div>❌ Request has been denied.</div> :
                                            <button
                                                onClick={(clickEvent) => { denyRequest(clickEvent, request.id) }}
                                                className="denyButton" id="button" >
                                                Deny Request
                                            </button>
                                        } */}

                                    </div>
                                </div>


                            </>)
                        })}
                    </div>
                </div> :

                <div>FatBack currently has no booking requests.</div>
            }

        </div >
        <div className="futureBookingsLink"
            onClick={() => {
                navigate("/futurebookings")
            }}>
            <p className="futureBookingText">See your <Link to="/futurebookings" id="link">future bookings </Link></p>
        </div>




    </>)
}

