import { useEffect, useState } from 'react'

export const ContactNotice = () => {


    const [contact, update] = useState({
        contacted: false
    })

    useEffect(() => {
        fetch('http://localhost:8088/requests?_expand=genre&_sort=startDate', {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(contact)
        })
    })



    return (
        <>
            <label>Email sent?</label>
            <input
                type="checkbox"
                value={contact.contacted}
                onChange={
                    (event) => {
                        const copy = { ...contact }
                        copy.contacted = event.target.checked
                        setContacted(copy)
                    }
                } />
        </>
    )
}
