import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { WorkingCalendar } from "./WorkingCalendar"
import "./Calendar.css"

export const Calender = () => {

    const [requests, setRequest] = useState([])

    const localFatBackUser = localStorage.getItem("fatback_user")
    const fatbackUserObject = JSON.parse(localFatBackUser)

    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?isAccepted=true`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(requestInfo)
                })
        }, []
    )


    const getAllRequests = () => {
        fetch(`http://localhost:8088/requests?_expand=genre&userId=${fatbackUserObject.id}`)
            .then(response => response.json())
            .then((requestInfo) => {
                setRequest(requestInfo)
            })
    }

    return (
        <>

            {requests.isAccepted === true
            ?  
            requests.map(request => { 
                return <div>{request.bandName}</div>  
            }) : ""}


        <WorkingCalendar /> 
        </>
    )
}
