import { useEffect, useState } from "react"

export const DenyReason = () => {

    const [reasons, setReasons] = useState([])

    useEffect(() => {
        fetch('http://localhost:8088/denyReasons')
            .then((reason) => {
                setReasons(reason)
            })

    }, [])

    return (<>
        <label htmlFor="reason">Choose a location:</label>
        <select id="reason"
            onChange={
                (event) => {
                    const copy = { ...setReasons }
                    copy.locationId = parseInt(event.target.value)
                    setReasons(copy)
                }}>
            <option value="0">Reason...</option>

            {
                reasons.map(
                    (reason) => {
                        return <option
                            key={`reason--${reason.id}`}
                            value={reason.id}>{reason.reason}</option>
                    }
                )
            }
        </select>
    </>)
}
