import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import "./BookingForm.css"

export const BookingForm = () => {

    const [genres, setGenres] = useState([])

    const [request, setBookingForm] = useState({

        bandName: "",
        phoneNumber: "",
        genreId: 0, 
        numOfSongs: ""

    })

    const navigate = useNavigate()

    const localFatBackUser = localStorage.getItem("fatback_user")
    const fatbackUserObject = JSON.parse(localFatBackUser)

    useEffect(
        () => {
            fetch(`http://localhost:8088/genres`)
                .then(response => response.json())
                .then((genreArray) => {
                    setGenres(genreArray)
                })
        },
        []

    )


    const submitButtonClick = (event) => {
        event.preventDefault()

        const requestFormToSendToAPI = {

            clientId: fatbackUserObject.id,
            bandName: request.bandName,
            phoneNumber: request.phoneNumber,
            genreId: request.genreId,
            numOfSongs: request.numOfSongs

        }

        return fetch(`http://localhost:8088/requests`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requestFormToSendToAPI)
        })

            .then(response => response.json())
            .then(() => {
                navigate("/myrequest")
            })

    }

    return (
        < div className="bookingForm" >
            <h2>Booking Form</h2>
            <form>

                <fieldset >
                    <div className="form-group">
                        <label htmlFor="bandName">Band Name: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={request.bandName}
                            onChange={
                                (event) => {
                                    const copy = { ...request }
                                    copy.bandName = event.target.value
                                    setBookingForm(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                <fieldset>
                    <div className="form-group">
                        <label htmlFor="phoneNumber">
                            Phone Number:
                        </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            placeholder="Your number..."
                            value={request.phoneNumber}
                            onChange={
                                (event) => {
                                    const copy = { ...request }
                                    copy.phoneNumber = event.target.value
                                    setBookingForm(copy)
                                }
                            }
                        />

                    </div>
                </fieldset>

                <fieldset>
                    <div className="form-group">
                        <label htmlFor="genre">Choose a genre:</label>
                        <select id="genre"
                            onChange={
                                (event) => {
                                    const copy = { ...request }
                                    copy.genreId = parseInt(event.target.value)
                                    setBookingForm(copy)
                                }}>
                            <option value="0">Genre...</option>

                            {
                                genres.map(
                                    (genre) => {
                                        return <option
                                            key={`genre--${genre.id}`}
                                            value={genre.id}>{genre.type}</option>
                                    }
                                )
                            }
                        </select>

                    </div>
                </fieldset>

                <fieldset>
                    <div className="form-group">
                        <label htmlFor="songs">
                            No. of Songs:
                        </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={request.numOfSongs}
                            onChange={
                                (event) => {
                                    const copy = { ...request }
                                    copy.numOfSongs = event.target.value
                                    setBookingForm(copy)
                                }
                            }

                        />

                    </div>
                </fieldset>

                <fieldset>
                    <div className="form-group">
                        <label htmlFor="date">
                            Start Date:
                        </label>
                        <input

                            type="date"

                            className="form-control"
                            onChange={
                                (event) => {
                                    const copy = { ...request }
                                    copy.startDate = event.target.value
                                    setBookingForm(copy)
                                }
                            }

                        />

                    </div>
                </fieldset>


                <button
                    onClick={(clickEvent) => {
                        submitButtonClick(clickEvent)
                    }}
                    className="submitButton">
                    Submit Request
                </button>


            </form>
        </div >
    )
}
