import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import "./Staff.css"

export const AddStaff = () => {

    const [staffMembers, setStaffMembers] = useState([])

    const [newStaff, setNewStaff] = useState({
        fullName: "",
        email: "",
        isStaff: false
    })

    const navigate = useNavigate()

    useEffect(() => {
        fetch(`http://localhost:8088/staff?&_expand=user`)
            .then(response => response.json())
            .then((userArray) => {
                setStaffMembers(userArray)
            })
    }, [])

    const handleSaveNewStaffButton = (event) => {
        event.preventDefault()

        const newUserToSendToAPI = {
            fullName: newStaff.fullName,
            email: newStaff.email,
            isStaff: true
        }

        const newStaffToSendToAPI = { 
            userId: fatbackUserObject.id, 
            startDate: newStaff.startDate, 
            imageURL: newStaff.imageURL
        }

        return fetch(`http://localhost:8088/users`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(newUserToSendToAPI)
        })

            .then((response) => response.json())
            .then(() => {
                navigate("/aboutFatBack")
            })

    }

    return (
        <div>
            <h2 className="addStaffHeader">Add New Staff Member</h2>
            <form className="newStaffForm">
                <fieldset >
                    <div className="form-group">
                        <label className="label" htmlFor="name">Name: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={newStaff.fullName}
                            onChange={
                                (event) => {
                                    const copy = { ...newStaff }
                                    copy.fullName = event.target.value
                                    setNewStaff(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                <fieldset >
                    <div className="form-group">
                        <label className="label" htmlFor="email">Email: </label>
                        <input
                            required autoFocus
                            type="text"
                            className="form-control"
                            value={newStaff.email}
                            onChange={
                                (event) => {
                                    const copy = { ...newStaff }
                                    copy.email = event.target.value
                                    setNewStaff(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                {/* <fieldset >
                    <div className="form-group">
                        <label className="label" htmlFor="title">Title:</label>
                        <select id="title" required
                            onChange={
                                (event) => {
                                    const copy = { ...newStaff }
                                    copy.titleId = parseInt(event.target.value)
                                    setNewStaff(copy)
                                }}>
                            <option value="0">Title...</option>

                            {
                                titles.map(
                                    (title) => {
                                        return <option
                                            key={`title--${title.id}`}
                                            value={title.id}
                                        >{title.type}
                                        </option>
                                    }
                                )
                            }
                        </select>

                    </div>
                </fieldset> */}

                {/* <fieldset >
                    <div className="form-group dateContainer">
                        <label className="label" htmlFor="startDate">Start Date: </label>
                        <input
                            required autoFocus
                            type="date"
                            className="form-control"
                            value={newStaff.startDate}
                            onChange={
                                (event) => {
                                    const copy = { ...newStaff }
                                    copy.startDate = event.target.value
                                    setNewStaff(copy)
                                }
                            }
                        />
                    </div>
                </fieldset> */}

                <fieldset >
                    <div className="form-group imageContainer">
                        <label className="label" htmlFor="image">Image: </label>
                        <input
                            required autoFocus
                            type="texxt"
                            className="form-control"
                            placeholder="example.com"
                            value={newStaff.imageURL}
                            onChange={
                                (event) => {
                                    const copy = { ...newStaff }
                                    copy.imageURL = event.target.value
                                    setNewStaff(copy)
                                }
                            }
                        />
                    </div>
                </fieldset>

                <button
                    className="saveNewStaffButton"
                    onClick={(event) => {
                        handleSaveNewStaffButton(event)
                    }}
                >
                    Add New Staff
                </button>
            </form>
        </div>
    )
}
