import { useEffect, useState } from 'react'
import { useNavigate } from "react-router-dom"
import "./Gallery.css"

export const GalleryView = () => {

    const [photos, setPhotos] = useState([])
    const localFatBackUser = localStorage.getItem("fatback_user")
    const fatbackUserObject = JSON.parse(localFatBackUser)

    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/gallery`)
                .then(response => response.json())
                .then((photoGallery) =>
                    setPhotos(photoGallery))
        },
        []
    )

    const getAllImages = () => { 

        fetch(`http://localhost:8088/gallery`)
    }

    const handleDeleteImageButton = () => {

        fetch(`http://localhost:8088/gallery/${photos.id}`, { 
            method: "DELETE"
        })
        .then(() => { 
            getAllImages()
        })

    }

    const addPhoto = (event) => {
        event.preventDefault()

        navigate(`/gallery/addgalleryimage`)

    }

    return (
        <div className="galleryPage">
            <h3 className="galleryHeader">Gallery</h3>
            <div className="gallery">
                {
                    photos.map(photo => {
                        return <div><img className="galleryImg" src={photo.imageURL} />
                            {fatbackUserObject.staff ?
                                <button id="button"
                                    onClick={(event) => {
                                        handleDeleteImageButton(event)
                                    }}>Delete</button> : ""
                            }
                        </div>

                    }
                    )
                }
            </div>


            {
                fatbackUserObject.staff
                    ?
                    <div className="button">
                        <button
                            onClick={(clickEvent) => { addPhoto(clickEvent, photos.id) }}
                            className="editButton">
                            Add New Photos
                        </button>


                    </div> :
                    <></>
            }
        </div>
    )
}
