import { useState, useEffect } from "react"
import { useNavigate, useParams } from "react-router-dom"
import "./BookingForm"

export const MyRequest = () => {

    const { clientId } = useParams()
    const [genres, setGenres] = useState([])
    const [client, setClients] = useState({})
    const navigate = useNavigate()


    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre`)
                .then(response => response.json())
                .then((genreInfo) => {
                    setGenres(genreInfo)
                })
        }, []
    )


    useEffect(
        () => {
            fetch(`http://localhost:8088/clients?&_embed=requests&id=${clientId}`)
                .then(response => response.json())
                .then((clientInfo) => {
                    const singleClient = clientInfo[0]
                    setClients(singleClient)
                })
        },
        [clientId]
    )

    const handleEditButtonClick = (event) => {
        event.preventDefault()

        return fetch(`http://localhost:8088/clients?&_embed=requests`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(requests)
        })

            .then((response) => response.json)
            .then(() => {
                navigate("/myrequest")
            })

    }

    return (
        <div className="myrequest">

            <h2>My Request</h2>
            <div>Band Name: {client?.request?.bandName}</div>
            <div>Start Date: {client?.request?.startDate}</div>
            <div>End Date: {client?.request?.endDate}</div>
            <div>Genre: {genres?.type}</div>

            <button
                onClick={(clickEvent) => { handleEditButtonClick(clickEvent) }}
                className="btn btn-primary">
                Save Edits
            </button>
        </div>

    )
}
