import React, { useState } from "react";
import "./ModalTest.css";

export const AdminModal = ({users}) => {
  const [modal, setModal] = useState(false);

  const toggleModal = () => {
    setModal(!modal)
  }

  if(modal) {
    document.body.classList.add('active-modal')
  } else {
    document.body.classList.remove('active-modal')
  }

  return (
    <>
      <button onClick={toggleModal} className="btn-modal">
      {/* <h1 id="title-main" onClick={toggleModal} className="btn-modal">FatBack Studio {users.fullName}</h1> */}
      Welcome FatBack Fam
      </button>

      {modal && (
        <div className="modal_window">
          <div onClick={toggleModal}></div>
            <h2 className="modal_titlebar">Hi staffMember</h2>
            {/* <p className="modal_content">
              You have new pending requests.
            </p> */}
            <button className="modal__close" onClick={toggleModal}>
              CLOSE
            </button>
          </div>
      )}

    </>
  )
}

