import { useEffect, useState } from 'react'
import { useNavigate } from "react-router-dom"
import "./Gallery.css"

export const GalleryView = () => {

    const [photos, setPhotos] = useState([])

    const localFatBackUser = localStorage.getItem("fatback_user")
    const fatbackUserObject = JSON.parse(localFatBackUser)

    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/gallery`)
                .then(response => response.json())
                .then((photoGallery) =>
                    setPhotos(photoGallery))
        },
        []
    )

    const addPhoto = (event, photo) => {
        event.preventDefault()

        navigate(`/gallery/${photo}`)

    }


    const deleteButton = (id) => {

        return <button
            onClick={() => {
                fetch(`http://localhost:8088/gallery/${id}`, {
                    method: "DELETE"
                })
                    .then(() => {
                        setPhotos()
                    })
            }}
        >Delete Photo</button>
    }
    // const deleteButton = (id) => {
    //     //if (!fatbackUserObject.staff) 
    //     return <button
    //         onClick={() => {
    //             fetch(`http://localhost:8088/requests/${id}`, {
    //                 method: "DELETE"
    //             })
    //                 .then(() => {
    //                     getAllRequests()
    //                 })
    //             // .then(() => {
    //             //     setTimeout(() => navigate("/"), 3000);
    //             // })
    //         }}
    //         className="deleteButton">
    //         Delete Request</button>
    // }

    return (
        <div className="galleryPage">
            <h3 className="galleryHeader">Gallery</h3>
            <div className="gallery">
                {
                    photos.map(photo => {
                        return <div><img className="galleryImg" src={photo.imageURL} />

                        </div>
                        {
                            fatbackUserObject.staff ?
                            deleteButton() : ""

                        }
                    })

                }
            </div>


            {
                fatbackUserObject.staff
                    ?
                    <div className="button">
                        <button
                            onClick={(clickEvent) => { addPhoto(clickEvent, photos.id) }}
                            className="editButton">
                            Add New Photos
                        </button>


                    </div> :
                    <></>
            }
        </div>
    )
}
