import { useEffect, useState } from 'react'
import "./Details.css"

export const StaffList = ({searchTermState}) => {

    const [staffMembers, setStaff] = useState([])
    const [filteredStaff, setFilteredStaff] = useState([])
    const [staffTitles, setTitle] = useState([])


    useEffect(
        () => {
            fetch(`http://localhost:8088/staff?&_expand=user&_sort=staff.fullName`)
                .then(response => response.json())
                .then((staffArray) => {
                    setStaff(staffArray)
                })
        },
        []
    )

    useEffect( 
        () => { 
            setFilteredStaff(staffMembers)
        }, 
        [staffMembers]
    )


    useEffect(
        () => {
            const searchedProducts = staffMembers.filter(staffMember => {
                return staffMember.title.toLowerCase().startsWith(searchTermState.toLowerCase())
            }
            )
            setFilteredStaff(searchedProducts)
        },
        [searchTermState]
    )

    useEffect(
        () => {
            fetch(`http://localhost:8088/staffTitles?&_expand=title&_expand=staff`)
                .then(response => response.json())
                .then((titleArray) => {
                    setTitle(titleArray)
                })

        },
        []
    )




    return (

        <div className="staffPage">
            <h2 className="staffHeader">Our Staff</h2>
            <div className="staffMembers">
                {
                    filteredStaff.map((staff) => {
                        return <div className="staff" key={`staff--${staff.id}`}>

                            <img src={staff.imageURL} />
                            <div className="fullName"> {staff?.user?.fullName}</div>
                            {staffTitles.map(staffTitle => {
                                if (staffTitle.staffId === staff.id) {
                                    return <div className="title">{staffTitle.title.type}</div>
                                }
                            })}

                        </div>
                    })
                }
            </div>
        </div>
    )
}
