import { useState, useEffect } from "react"
import { useNavigate, Link } from "react-router-dom"
import { FutureBookings } from "./FutureBookings"
import {AcceptDenyButton} from "./AcceptDenyButton"
import "./BookingForm"


export const Requests = () => {

    const [requests, setRequest] = useState([])
    const [refresh, setRefresh] = useState(true)


    const navigate = useNavigate()

    // const localFatBackUser = localStorage.getItem("fatback_user")
    // const fatbackUserObject = JSON.parse(localFatBackUser)

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests?_expand=genre`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(requestInfo)
                })
        }, [, refresh]
    )


    // const denyRequest = (event, requests) => {
    //     event.preventDefault()

    //     const requestDenyStatusForAPI = {
    //         isDenied: true,
    //         isPending: false, 
    //     }

    //     return fetch(`http://localhost:8088/requests/${requests}`, {
    //         method: "PATCH",
    //         headers: {
    //             "Content-Type": "application/json",
    //         },
    //         body: JSON.stringify(requestDenyStatusForAPI)
    //     })
    //         .then(response => response.json())
    //         .then(
    //             <button
    //                 onClick={(clickEvent) => { acceptRequestButton(clickEvent, requests.id) }}
    //                 className="acceptButton" id="button">
    //                 Accept Request
    //             </button>
    //         )
    //         .then(
    //             setRefresh(!refresh)
    //         )

    // }

    return (<>

        <div className="myrequest">
            {requests.length ?
                <div key={`requests--${requests.id}`}>
                    <h2 className="requestHeader">Studio Requests</h2>

                    {requests.map(request => {
                        return (<>
                            <div className="requestInfo">
                                <div>Band Name: {request.bandName}</div>
                                <div>Phone Number: {request.phoneNumber}</div>
                                <div>Start Date: {request.startDate}</div>
                                <div>End Date: {request.endDate}</div>
                                <div>No. of Songs: {request.numOfSongs}</div>
                                <div>Genre: {request?.genre?.type}</div>

                                <div className="radioReview">Musicians:
                                    {request.musicianRequest === true ?
                                        <div>Yes</div> :
                                        <div>No</div>
                                    }

                                </div>

                                <div className="radioReview">Session Musicians:
                                    {request.sessionMusicians === true ?
                                        <div>Yes</div> :
                                        <div> No</div>
                                    }

                                </div>

                                <div className="radioReview">Photos:
                                    {request.photoRequest === true ?
                                        <div>Yes</div> :
                                        <div>No</div>
                                    }

                                </div>

                                <div className="radioReview">Video Request:
                                    {request.videoRequest === true ?
                                        <div>Yes</div> :
                                        <div>No</div>
                                    }

                                </div>

                                {/* <div className="status">
                                    {requests.isAccepted === false 
                                    ? <div>Status: pending</div>:
                                    <div>Accepted!</div>}
                                </div> */}

                                <div className="buttonContainer">

                                    {/* {request.isDenied === true
                                        ? <div>❌ Request has been denied.</div> :
                                        <button
                                            onClick={(clickEvent) => { denyRequest(clickEvent, request.id) }}
                                            className="denyButton" id="button" >
                                            Deny Request
                                        </button>
                                    } */}
                                </div>
                            </div>


                        </>)
                    })}
                </div> :

                <div>FatBack currently has no booking requests.</div>
            }

            <div className="futureBookingsLink"
                onClick={() => {
                    navigate("/futurebookings")
                }}>
                <p>See your <Link to="/futurebookings" id="link">future bookings </Link></p>
            </div>

            <FutureBookings requests={requests} />
            <AcceptDenyButton requests={requests}/> 

        </div >

    </>)
}
