import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import "./BookingForm"

export const Requests = () => {

    const [requests, setRequest] = useState([])

    const localFatBackUser = localStorage.getItem("fatback_user")
    const fatbackUserObject = JSON.parse(localFatBackUser)

    const navigate = useNavigate()

    useEffect(
        () => {
            fetch(`http://localhost:8088/requests`)
                .then(response => response.json())
                .then((requestInfo) => {
                    setRequest(requestInfo)
                })
        }, []
    )


    const getAllRequests = () => {
        fetch(`http://localhost:8088/requests?_expand=genre&userId=${fatbackUserObject.id}`)
            .then(response => response.json())
            .then((requestInfo) => {
                setRequest(requestInfo)
            })
    }

    const acceptRequestButton = (event, request) => {
        event.preventDefault()

        return <button className="acceptButton"
            onClick={
                () => {
                    fetch(`http://localhost:8088/requests`, {
                        method: "POST",
                        headers: {
                            "Content-Type": "application/json"
                        },
                        body: JSON.stringify({
                            isAccepted: false
                        })
                    })
                        .then(response => response.json())

                        // this is what will happen after ticket has been claimed & API has been updated 
                        // need to GET the state from API again
                        // this function is created in TicketList.js

                        .then(getAllRequests)
                }
            }>Accept Request</button>
    }




    // const denyRequest = () => {

    //     return <button
    //         onClick={() =>
    //             fetch(`http://localhost:8088/requests?_expand=genre&userId=${fatbackUserObject.id}`, {
    //                 method: "DELETE"
    //             })

    //                 .then(() => {
    //                     getAllRequests()
    //                 })
    //         }
    //         className="denyButton">
    //         Deny Request</button>

    // }

    return (<>

        <div className="myrequest">
            {requests.length ?
                <div key={`requests--${requests.id}`}>
                    <h2 className="requestHeader">Studio Requests</h2>
                    {requests.map(request => {
                        return (<>
                            <div className="requestInfo">
                                <div>Band Name: {request.bandName}</div>
                                <div>Phone Number: {request.phoneNumber}</div>
                                <div>Start Date: {request.startDate}</div>
                                <div>End Date: {request.endDate}</div>
                                <div>No. of Songs: {request.numOfSongs}</div>
                                <div>Genre: {request?.genre?.type}</div>

                                <div className="radioReview">Musicians:
                                    {request.musicianRequest === true ?
                                        <div>Yes</div> :
                                        <div>No</div>
                                    }

                                </div>

                                <div className="radioReview">Session Musicians:
                                    {request.sessionMusicians === true ?
                                        <div>Yes</div> :
                                        <div> No</div>
                                    }

                                </div>

                                <div className="radioReview">Photos:
                                    {request.photoRequest === true ?
                                        <div>Yes</div> :
                                        <div>No</div>
                                    }

                                </div>

                                <div className="radioReview">Video Request:
                                    {request.videoRequest === true ?
                                        <div>Yes</div> :
                                        <div>No</div>
                                    }

                                </div>
                            </div>
                            <>
                                <div className="buttonContainer">
                                    <button
                                    onClick={(clickEvent) => { acceptRequestButton(clickEvent, request.id) }}
                                    className="acceptButton">
                                    Accept Request
                                </button>
                                    {/* {
                                        requests.isAccepted === true
                                            ? `Your request has been accepted!` :
                                            acceptRequestButton(request.id)
                                    } */}

                                    {/* {request.length
                                        ? `Request has been denied.` : denyRequest()
                                    } */}



                                </div>
                            </>

                        </>)
                    })}
                </div> :

                <div>FatBack currently has no booking requests.</div>
            }
        </div >

    </>)
}
